var webdriverio = require('./');
var client = webdriverio.remote({
    logLevel: 'silent',
    host: '0.0.0.0',
    port: 4444,
    waitforTimeout: 500,
    coloredLogs: true,
    desiredCapabilities: {
        browserName: 'phantomjs'
    }
});

client
    .init()
    .url('http://localhost:8080/test/site/www/')
    .logTypes().then(console.log)
    .log('driver').then(console.log)
    .end();